import "core-js/modules/web.dom-exception.stack.js";
import { defineComponent } from 'vue';
export default defineComponent({
  name: "EventList22nd",
  data() {
    return {
      itemList: [],
      eventList: [],
      currentPage: 1,
      maxPerPage: 20
    };
  },
  async mounted() {
    await this.getItemList();
    await this.getEventList();
  },
  methods: {
    decodeBase64(str) {
      if (str === null) return null;
      return atob(str.replace(/_/g, "/").replace(/-/g, "+"));
    },
    getDiscountPricePercentage(item) {
      return Math.round((item.orgprice - item.sellcash) / item.orgprice * 100);
    },
    moveToProductPage(targetId) {
      location.href = "https://m.10x10.co.kr//category/category_itemPrd.asp?itemid=" + targetId;
    },
    async getItemList() {
      try {
        const targetUrl = `https://gateway.10x10.co.kr/api/web/v1/event/sub-category-of-items?masterCode=31&detailCodes=0&deviceType=MOBILE`;
        const response = await fetch(targetUrl);
        if (response.ok) {
          const json = await response.json();
          if (json.status === 200) {
            this.itemList = json.result;
            this.itemList.slice(0, this.currentPage * this.maxPerPage);
          }
        } else {
          new Error('');
        }
      } catch (e) {}
    },
    async getEventList() {
      try {
        const targetUrl = `https://gateway.10x10.co.kr/v1/event/apis/exhibition-event-group/31`;
        const response = await fetch(targetUrl);
        if (response.ok) {
          const json = await response.json();
          if (json.status === 200) {
            this.eventList = json.result;
          }
        }
      } catch (e) {}
    }
  }
});
